package Basics;

public class AugmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 1;
		x+=5;
		System.out.println(x);
		int y = 4;
		y-=6;
		System.out.println(y);
		x*=3;
		System.out.println(x);
		y/=2;
		System.out.println(y);
		

	}

}
